func testOptionKindToManyAddOpKindOptions(t *testing.T) {
	var err error

	ctx := context.Background()
	tx := MustTx(boil.BeginTx(ctx, nil))
	defer func() { _ = tx.Rollback() }()

	var a OptionKind
	var b, c, d, e Option

	seed := randomize.NewSeed()
	if err = randomize.Struct(seed, &a, optionKindDBTypes, false, strmangle.SetComplement(optionKindPrimaryKeyColumns, optionKindColumnsWithoutDefault)...); err != nil {
		t.Fatal(err)
	}
	foreigners := []*Option{&b, &c, &d, &e}
	for _, x := range foreigners {
		if err = randomize.Struct(seed, x, optionDBTypes, false, strmangle.SetComplement(optionPrimaryKeyColumns, optionColumnsWithoutDefault)...); err != nil {
			t.Fatal(err)
		}
	}

	if err := a.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}
	if err = b.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}
	if err = c.Insert(ctx, tx, boil.Infer()); err != nil {
		t.Fatal(err)
	}

	foreignersSplitByInsertion := [][]*Option{
		{&b, &c},
		{&d, &e},
	}

	for i, x := range foreignersSplitByInsertion {
		err = a.AddKindOptions(ctx, tx, i != 0, x...)
		if err != nil {
			t.Fatal(err)
		}

		first := x[0]
		second := x[1]

		if !queries.Equal(a.ID, first.KindID) {
			t.Error("foreign key was wrong value", a.ID, first.KindID)
		}
		if !queries.Equal(a.ID, second.KindID) {
			t.Error("foreign key was wrong value", a.ID, second.KindID)
		}

		if first.R.Kind != &a {
			t.Error("relationship was not added properly to the foreign slice")
		}
		if second.R.Kind != &a {
			t.Error("relationship was not added properly to the foreign slice")
		}

		if a.R.KindOptions[i*2] != first {
			t.Error("relationship struct slice not set to correct value")
		}
		if a.R.KindOptions[i*2+1] != second {
			t.Error("relationship struct slice not set to correct value")
		}

		count, err := a.KindOptions().Count(ctx, tx)
		if err != nil {
			t.Fatal(err)
		}
		if want := int64((i + 1) * 2); count != want {
			t.Error("want", want, "got", count)
		}
	}
}

